const btn_dark = document.querySelector(".btn-dark");
const currentTheme = localStorage.getItem("theme");
if (currentTheme == "dark") {
    document.body.classList.add("dark-theme");
    btn_dark.innerHTML = `<svg class='line' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24'><g><circle class='line' cx='12' cy='12' r='6'></circle><circle cx='12' cy='3' r='1'></circle><circle cx='12' cy='21' r='1'></circle><circle cx='21' cy='12' r='1'></circle><circle cx='3' cy='12' r='1'></circle><circle cx='5.5' cy='5.5' r='1'></circle><circle cx='18.5' cy='18.5' r='1'></circle><circle cx='18.5' cy='5.5' r='1'></circle><circle cx='5.5' cy='18.5' r='1'></circle></g></svg>`;
} else {
    btn_dark.innerHTML = `<svg class='line' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24'><g><path d='M183.72453,170.371a10.4306,10.4306,0,0,1-.8987,3.793,11.19849,11.19849,0,0,1-5.73738,5.72881,10.43255,10.43255,0,0,1-3.77582.89138,1.99388,1.99388,0,0,0-1.52447,3.18176,10.82936,10.82936,0,1,0,15.118-15.11819A1.99364,1.99364,0,0,0,183.72453,170.371Z' transform='translate(-169.3959 -166.45548)'></path></g></svg>`;
}
btn_dark.addEventListener("click", function (e) {
    e.preventDefault();
    this.classList.toggle("dark-actived");
    document.body.classList.toggle("dark-theme");
    let theme = "light";
    if (document.body.classList.contains("dark-theme")) {
        theme = "dark";
        this.innerHTML = `<svg class='line' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24'><g><circle class='line' cx='12' cy='12' r='6'></circle><circle cx='12' cy='3' r='1'></circle><circle cx='12' cy='21' r='1'></circle><circle cx='21' cy='12' r='1'></circle><circle cx='3' cy='12' r='1'></circle><circle cx='5.5' cy='5.5' r='1'></circle><circle cx='18.5' cy='18.5' r='1'></circle><circle cx='18.5' cy='5.5' r='1'></circle><circle cx='5.5' cy='18.5' r='1'></circle></g></svg>`;
    } else {
        this.innerHTML = `<svg class='line' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24'><g><path d='M183.72453,170.371a10.4306,10.4306,0,0,1-.8987,3.793,11.19849,11.19849,0,0,1-5.73738,5.72881,10.43255,10.43255,0,0,1-3.77582.89138,1.99388,1.99388,0,0,0-1.52447,3.18176,10.82936,10.82936,0,1,0,15.118-15.11819A1.99364,1.99364,0,0,0,183.72453,170.371Z' transform='translate(-169.3959 -166.45548)'></path></g></svg>`;
