const outsider = document.getElementById('popular-slide');
const distance = 480;
function scrollLft() {
  outsider.scrollBy({
    left: -distance,
    behavior: 'smooth'
  });
}
function scrollRight() {
  outsider.scrollBy({
    left: distance,
    behavior: 'smooth'
  });
