function avatar() {
/* Avatar Theme
	/* ---------------------------------------------------*/
$(document).ready(function() {
    $(".comments .avatar-image-container").find("img").each(function(a, t) {
    (t = $(t)).attr({
    src: t.attr("src").replace("/s35" , "/s200")
    })
    })
});
}
function img() {
(() => {
    window.LightBox = new function () {
        this.target = '[lightbox] img';
        this.listener = (e) => {
            if ( e.ctrlKey || e.metaKey || e.shiftKey || e.altKey ) return;
            const _class = String(this.target.split(',').map(_ => `${_.trim()}:not([no-view])`));
            const el = e.target.closest(_class);
            if (!el) return;
            const contain = el.closest('[lightbox]') || document.body;
            const images = [...contain.querySelectorAll(_class)].map(_ => _.href || _.src);
            this.display(images, el.href || el.src);
            e.stopPropagation();
            e.preventDefault();
        };
        this.init = (val) => {
            if (val) this.target = val;
            ['removeEventListener', 'addEventListener'].forEach(method => {
                document[method]('click', this.listener, false);
            });
        }
        this.display = (images, src) => {
            let index = images.indexOf(src);
            const $el = new DOMParser().parseFromString(`
                <div class="lightbox">
                    <div class="lightbox-container">
                        <div class="lightbox-lead"></div>
                        <div class="lightbox-close lightbox-close-full"></div>
                    </div>
                    <div class="lightbox-tools">
                        <div class="lightbox-tools-count">
                            <span><span class="lightbox-index">${index + 1}</span>/${images.length}</span>
                        </div>
                        <div class="lightbox-tools-flip">
                            <div class="lightbox-btn lightbox-tools-flip-prev">
                                <svg class='line' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24'><g transform='translate(12.000000, 12.000000) rotate(-270.000000) translate(-12.000000, -12.000000) translate(5.000000, 8.500000)'><path d='M14,0 C14,0 9.856,7 7,7 C4.145,7 0,0 0,0'></path></g></svg>
                            </div>
                            <div class="lightbox-btn lightbox-tools-flip-next">
						<svg class='line' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24'><g transform='translate(12.000000, 12.000000) rotate(-90.000000) translate(-12.000000, -12.000000) translate(5.000000, 8.500000)'><path d='M14,0 C14,0 9.856,7 7,7 C4.145,7 0,0 0,0'></path></g></svg>
                            </div>
                        </div>
                        <div class="lightbox-btn lightbox-close">
                            <svg class='line' viewBox="0 0 48 48" xmlns="http://www.w3.org/2000/svg"><path d="M8 8L40 40"/><path d="M8 40L40 8"/></svg>
                        </div>
                    </div>
                </div>
            `, 'text/html').body.firstChild,
                keyFn = function (e) {
                    const keyMap = { Escape: 'close', ArrowLeft: 'tools-flip-prev', ArrowRight: 'tools-flip-next' };
                    if (keyMap[e.key]) $el.querySelector(`.lightbox-${keyMap[e.key]}`).click();
                },
                loadImage = (src) => {
                    const img = new Image(), $lead = $el.querySelector('.lightbox-lead');
                    $lead.className = "lightbox-lead lightbox-lead-out";
                    setTimeout(() => {
                        $lead.innerHTML = "";
                        img.onload = function () {
                            setTimeout(() => {
                                $lead.innerHTML = `<img src="${img.src}" alt="LightBox" no-view/>`;
                                $lead.className = "lightbox-lead lightbox-lead-in";
                            }, 100);
                        };
                        img.src = src;
                    }, 300);
                };
            document.body.appendChild($el);
            loadImage(src);

            window.addEventListener("keydown", keyFn);
            $el.onclick = (e) => {
                if (e.target.closest('.lightbox-close')) {
                    window.removeEventListener("keydown", keyFn);
                    $el.onclick = null;
                    $el.classList.add('lightbox-out');
                    setTimeout(() => $el.remove(), 290);
                } else if (e.target.closest('.lightbox-tools-flip')) {
                    if (e.target.closest('.lightbox-tools-flip-prev')) {
                        index = index === 0 ? images.length - 1 : index - 1;
                    } else {
                        index = index === images.length - 1 ? 0 : index + 1;
                    }
                    loadImage(images[index]);
                    $el.querySelector('.lightbox-index').innerHTML = index + 1;
                }
            };
        }
    }
})();
}
function lightbox() {window.LightBox && LightBox.init('.post-body img');}
function video() {
/* Enable full width mode on img
	------------------------------------------------------*/
	function bynImgExpand(){
		var innerViewport = $('body').innerWidth();
		var innerViewportHalf = $('body').innerWidth() / 2;

		$("img[alt*='full image']").each(function(i){
			$(this).parents('.tr-caption-container').addClass('img-gallery-full');
			$(this).parents('.tr-caption-container').css({
				'width': innerViewport,
				'margin-right': 'calc(50% - ' + innerViewportHalf + 'px)',
				'margin-left': 'calc(50% - ' + innerViewportHalf + 'px)',
			});
		});
		$("img[alt*='wide image']").each(function(i){
			$(this).parents('.tr-caption-container').addClass('img-gallery-wide');
			$(this).parents('.tr-caption-container').css({
				'width': innerViewport,
				'margin-right': 'calc(50% - ' + innerViewportHalf + 'px)',
				'margin-left': 'calc(50% - ' + innerViewportHalf + 'px)',
			});
		});
	}
	$(window).resize(bynImgExpand);
/* Responsive Video frame
	/* ---------------------------------------------------*/
	/*
	Find all iFrames
	*/
	var $iframes = $( ".post-body iframe" );

	/*
	Set the iFrame aspect ratio
	*/
	$iframes.each(function () {
		$( this ).data( "ratio", this.height / this.width )
			.removeAttr( "width" )
			.removeAttr( "height" );
	});

		/*
		Set the iFrame aspect ratio
		*/
		$( window ).resize( function () {
			$iframes.each( function() {
				// Get the parent container's width
				var width = $( this ).parent().width();
				$( this ).width( width )
					.height( width * $( this ).data( "ratio" ) );
			});
		}).resize();
}
function comment() {
/* Comment User Name
	/* ---------------------------------------------------*/
	$(".comment-thread > ol > .comment").each(function() {
		var str = $(this).children(".comment-block").find(".comment-header > .user").text();
		$(this).find(".avatar-image-container").append("<span>" + str.charAt(0) + "</span>");
	});
}
function popup() {
$(document).ready(function(){
    $('.open').click(function(){
        $('cmt').fadeIn('200');
    });
    $('.close').click(function(event) {
        $('cmt').fadeOut('100');
    });
    $(document).on('keydown', function(e) {
        if (e.keyCode === 27) { // ESC
          $('cmt').hide();
        }
    });
});
}
